import React, { useEffect, useState } from 'react';
import axios from 'axios';
import '.styles/Fabricantes.css'; // Importando o arquivo de estilo para o popup

const Fabricantes = () => {
  const [fabricantes, setFabricantes] = useState([]);
  const [form, setForm] = useState({
    nome: '',
    nome_vendedor: '',
    contato_vendedor: ''
  });
  const [editandoId, setEditandoId] = useState(null);
  const [showPopup, setShowPopup] = useState(false); // Estado para controlar a exibição do popup

  const usuarioLogado = 'admin'; // Simule aqui o usuário logado

  useEffect(() => {
    buscarFabricantes();
  }, []);

  const buscarFabricantes = async () => {
    try {
      const res = await axios.get('/api/fabricantes');
      setFabricantes(res.data);
    } catch (error) {
      console.error('Erro ao buscar fabricantes:', error);
    }
  };

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (editandoId) {
        await axios.put(`/api/fabricantes/${editandoId}`, {
          ...form,
          atualizado_por: usuarioLogado
        });
      } else {
        await axios.post('/api/fabricantes', {
          ...form,
          criado_por: usuarioLogado,
          atualizado_por: usuarioLogado
        });
      }
      setForm({ nome: '', nome_vendedor: '', contato_vendedor: '' });
      setEditandoId(null);
      setShowPopup(false); // Fecha o popup após salvar
      buscarFabricantes();
    } catch (error) {
      console.error('Erro ao salvar fabricante:', error);
    }
  };

  const editarFabricante = (fabricante) => {
    setForm({
      nome: fabricante.nome,
      nome_vendedor: fabricante.nome_vendedor,
      contato_vendedor: fabricante.contato_vendedor
    });
    setEditandoId(fabricante.id);
    setShowPopup(true); // Abre o popup em modo de edição
  };

  const excluirFabricante = async (id) => {
    if (confirm('Deseja realmente excluir este fabricante?')) {
      try {
        await axios.delete(`/api/fabricantes/${id}`);
        buscarFabricantes();
      } catch (error) {
        console.error('Erro ao excluir fabricante:', error);
      }
    }
  };

  return (
    <div className="container">
      <h2>Cadastro de Fabricantes</h2>

      {/* Botão para abrir o popup */}
      <button onClick={() => setShowPopup(true)}>Cadastrar Fabricante</button>

      {/* Popup de cadastro */}
      {showPopup && (
        <div className="popup">
          <div className="popup-content">
            <h3>{editandoId ? 'Editar Fabricante' : 'Cadastrar Fabricante'}</h3>
            <form onSubmit={handleSubmit}>
              <div>
                <label>Nome do Fabricante:</label>
                <input
                  type="text"
                  name="nome"
                  placeholder="Nome do fabricante"
                  value={form.nome}
                  onChange={handleChange}
                  required
                />
              </div>
              <div>
                <label>Nome do Vendedor:</label>
                <input
                  type="text"
                  name="nome_vendedor"
                  placeholder="Nome do vendedor"
                  value={form.nome_vendedor}
                  onChange={handleChange}
                />
              </div>
              <div>
                <label>Contato do Vendedor:</label>
                <input
                  type="text"
                  name="contato_vendedor"
                  placeholder="Contato do vendedor"
                  value={form.contato_vendedor}
                  onChange={handleChange}
                />
              </div>
              <button type="submit">{editandoId ? 'Atualizar' : 'Cadastrar'}</button>
              <button type="button" onClick={() => setShowPopup(false)}>
                Cancelar
              </button>
            </form>
          </div>
        </div>
      )}

      <hr />

      <h3>Lista de Fabricantes</h3>
      <table>
        <thead>
          <tr>
            <th>Nome</th>
            <th>Vendedor</th>
            <th>Contato</th>
            <th>Criado por</th>
            <th>Criado em</th>
            <th>Atualizado por</th>
            <th>Atualizado em</th>
            <th>Ações</th>
          </tr>
        </thead>
        <tbody>
          {fabricantes.map((fab) => (
            <tr key={fab.id}>
              <td>{fab.nome}</td>
              <td>{fab.nome_vendedor}</td>
              <td>{fab.contato_vendedor}</td>
              <td>{fab.criado_por}</td>
              <td>{new Date(fab.criado_em).toLocaleString()}</td>
              <td>{fab.atualizado_por}</td>
              <td>{new Date(fab.atualizado_em).toLocaleString()}</td>
              <td>
                <button onClick={() => editarFabricante(fab)}>Editar</button>
                <button onClick={() => excluirFabricante(fab.id)}>Excluir</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Fabricantes;
