// frontend/src/pages/Usuarios.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';

export default function Usuarios() {
  const [usuarios, setUsuarios] = useState([]);
  const [editUser, setEditUser] = useState(null); // {id,nome,email,perfil}
  const [form, setForm]       = useState({ nome:'', email:'', senha:'', perfil:'usuario' });

  const token = localStorage.getItem('fswToken');
  const headers = { Authorization: `Bearer ${token}` };

  // Carrega a lista de usuários
  const fetchUsers = async () => {
    try {
      const { data } = await axios.get('http://localhost:5000/api/auth/users', { headers });
      setUsuarios(data);
    } catch (err) {
      console.error('Erro ao buscar usuários:', err);
    }
  };

  useEffect(() => { fetchUsers(); }, []);

  // Deleta um usuário
  const handleDelete = async (id) => {
    if (!window.confirm('Excluir este usuário?')) return;
    try {
      await axios.delete(`http://localhost:5000/api/auth/users/${id}`, { headers });
      fetchUsers();
    } catch (err) {
      console.error('Erro ao excluir usuário:', err);
    }
  };

  // Inicia edição de usuário
  const startEdit = (user) => {
    setEditUser(user);
    setForm({ nome: user.nome, email: user.email, senha: '', perfil: user.perfil });
  };

  // Trata mudança nos inputs
  const handleChange = e => {
    const { name, value } = e.target;
    setForm(f => ({ ...f, [name]: value }));
  };

  // Envia formulário de criar ou editar
  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (editUser) {
        // Atualiza
        await axios.put(
          `http://localhost:5000/api/auth/users/${editUser.id}`,
          form,
          { headers }
        );
      } else {
        // Cria novo
        await axios.post(
          `http://localhost:5000/api/auth/register`,
          form,
          { headers }
        );
      }
      setEditUser(null);
      setForm({ nome:'', email:'', senha:'', perfil:'usuario' });
      fetchUsers();
    } catch (err) {
      console.error('Erro ao salvar usuário:', err);
    }
  };

  return (
    <div>
      <h2>Usuários</h2>

      <table>
        <thead>
          <tr>
            <th>Nome</th><th>E‑mail</th><th>Perfil</th><th>Ações</th>
          </tr>
        </thead>
        <tbody>
          {usuarios.map(u => (
            <tr key={u.id}>
              <td>{u.nome}</td>
              <td>{u.email}</td>
              <td>{u.perfil}</td>
              <td>
                <button onClick={() => startEdit(u)}>Editar</button>
                <button onClick={() => handleDelete(u.id)}>Excluir</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      <h3>{editUser ? 'Editar' : 'Criar'} Usuário</h3>
      <form onSubmit={handleSubmit}>
        <input
          name="nome"
          placeholder="Nome"
          value={form.nome}
          onChange={handleChange}
          required
        />
        <input
          name="email"
          type="email"
          placeholder="E‑mail"
          value={form.email}
          onChange={handleChange}
          required
        />
        <input
          name="senha"
          type="password"
          placeholder={editUser ? 'Nova Senha (opcional)' : 'Senha'}
          value={form.senha}
          onChange={handleChange}
          required={!editUser}
        />
        <select name="perfil" value={form.perfil} onChange={handleChange}>
          <option value="usuario">Usuário</option>
          <option value="tecnico">Técnico</option>
          <option value="admin">Admin</option>
        </select>
        <button type="submit">{editUser ? 'Salvar' : 'Criar'}</button>
        {editUser && (
          <button
            type="button"
            onClick={() => {
              setEditUser(null);
              setForm({ nome:'', email:'', senha:'', perfil:'usuario' });
            }}
          >
            Cancelar
          </button>
        )}
      </form>
    </div>
  );
}
