// src/pages/Equipamentos.js
import '../styles/Equipamentos.css';
import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Equipamentos = () => {
  const [fabricantes, setFabricantes] = useState(['Fabricante A', 'Fabricante B', 'Fabricante C']);
  const [equipamentos, setEquipamentos] = useState([]);
  const [novoEquipamento, setNovoEquipamento] = useState({
    nome: '',
    codigo: '',
    descricao: '',
    fabricante: '',
    categoria: '',
    estoque: 0,
    status: ''
  });
  const [equipamentoEditando, setEquipamentoEditando] = useState(null);
  const [mostrarFormulario, setMostrarFormulario] = useState(false);
  const [mostrarFormularioEdicao, setMostrarFormularioEdicao] = useState(false);

  // Simulação do usuário autenticado
  const usuarioLogado = 'Admin';

  useEffect(() => {
    const fetchEquipamentos = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/equipamentos');
        setEquipamentos(response.data);
      } catch (error) {
        console.error('Erro ao buscar equipamentos', error);
      }
    };
    fetchEquipamentos();
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setNovoEquipamento((prevState) => ({
      ...prevState,
      [name]: value
    }));
  };

  const handleChangeEdicao = (e) => {
    const { name, value } = e.target;
    setEquipamentoEditando((prevState) => ({
      ...prevState,
      [name]: value
    }));
  };

  const handleSubmitCadastro = async (e) => {
    e.preventDefault();
    try {
      const equipamentoComUsuario = {
        ...novoEquipamento,
        criado_por: usuarioLogado
      };
      const response = await axios.post('http://localhost:5000/api/equipamentos', equipamentoComUsuario);
      setEquipamentos((prevState) => [...prevState, response.data]);
      setNovoEquipamento({
        nome: '',
        codigo: '',
        descricao: '',
        fabricante: '',
        categoria: '',
        estoque: 0,
        status: ''
      });
      setMostrarFormulario(false);
    } catch (error) {
      console.error('Erro ao cadastrar equipamento', error);
    }
  };

  const handleSubmitEdicao = async (e) => {
    e.preventDefault();
    try {
      const equipamentoAtualizado = {
        ...equipamentoEditando,
        atualizado_por: usuarioLogado
      };
      const response = await axios.put(`http://localhost:5000/api/equipamentos/${equipamentoEditando.id}`, equipamentoAtualizado);
      setEquipamentos((prevState) =>
        prevState.map((equipamento) =>
          equipamento.id === equipamentoEditando.id ? response.data : equipamento
        )
      );
      setEquipamentoEditando(null);
      setMostrarFormularioEdicao(false);
    } catch (error) {
      console.error('Erro ao editar equipamento', error);
    }
  };

  const handleEditClick = (equipamento) => {
    setEquipamentoEditando(equipamento);
    setMostrarFormularioEdicao(true);
  };

  const formatarData = (data) => {
    return new Date(data).toLocaleString('pt-BR', {
      timeZone: 'America/Sao_Paulo',
      day: '2-digit',
      month: '2-digit',
      year: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  return (
    <div>
      <h2>Equipamentos</h2>

      <table>
        <thead>
          <tr>
            <th>Nome</th>
            <th>Código</th>
            <th>Descrição</th>
            <th>Categoria</th>
            <th>Estoque</th>
            <th>Status</th>
            <th>Fabricante</th>
            <th>Criado por</th>
            <th>Criado em</th>
            <th>Atualizado por</th>
            <th>Atualizado em</th>
            <th>Ações</th>
          </tr>
        </thead>
        <tbody>
          {equipamentos.map((equipamento) => (
            <tr key={equipamento.id}>
              <td>{equipamento.nome}</td>
              <td>{equipamento.codigo}</td>
              <td>{equipamento.descricao}</td>
              <td>{equipamento.categoria}</td>
              <td>{equipamento.estoque}</td>
              <td>{equipamento.status}</td>
              <td>{equipamento.fabricante}</td>
              <td>{equipamento.criado_por}</td>
              <td>{equipamento.criado_em ? formatarData(equipamento.criado_em) : ''}</td>
              <td>{equipamento.atualizado_por}</td>
              <td>{equipamento.atualizado_em ? formatarData(equipamento.atualizado_em) : ''}</td>
              <td>
                <button onClick={() => handleEditClick(equipamento)}>Editar</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      <button onClick={() => setMostrarFormulario(true)}>Cadastrar Equipamento</button>

      {mostrarFormulario && (
        <div className="modal">
          <div className="modal-content">
            <h3>Cadastrar Novo Equipamento</h3>
            <form onSubmit={handleSubmitCadastro}>
              <input name="nome" value={novoEquipamento.nome} onChange={handleChange} placeholder="Nome" required />
              <input name="codigo" value={novoEquipamento.codigo} onChange={handleChange} placeholder="Código" />
              <textarea name="descricao" value={novoEquipamento.descricao} onChange={handleChange} placeholder="Descrição" />
              <select name="categoria" value={novoEquipamento.categoria} onChange={handleChange} required>
                <option value="">Categoria</option>
                <option value="equipamento">Equipamento</option>
                <option value="periferico">Periférico</option>
              </select>
              <input type="number" name="estoque" value={novoEquipamento.estoque} onChange={handleChange} placeholder="Estoque" />
              <select name="status" value={novoEquipamento.status} onChange={handleChange} required>
                <option value="">Status</option>
                <option value="Disponível">Disponível</option>
                <option value="Em Manutenção">Em Manutenção</option>
                <option value="Indisponível">Indisponível</option>
              </select>
              <select name="fabricante" value={novoEquipamento.fabricante} onChange={handleChange} required>
                <option value="">Fabricante</option>
                {fabricantes.map((fab, idx) => (
                  <option key={idx} value={fab}>{fab}</option>
                ))}
              </select>
              <button type="submit">Cadastrar</button>
              <button type="button" onClick={() => setMostrarFormulario(false)}>Cancelar</button>
            </form>
          </div>
        </div>
      )}

      {mostrarFormularioEdicao && equipamentoEditando && (
        <div className="modal">
          <div className="modal-content">
            <h3>Editar Equipamento</h3>
            <form onSubmit={handleSubmitEdicao}>
              <input name="nome" value={equipamentoEditando.nome} onChange={handleChangeEdicao} placeholder="Nome" required />
              <input name="codigo" value={equipamentoEditando.codigo} onChange={handleChangeEdicao} placeholder="Código" />
              <textarea name="descricao" value={equipamentoEditando.descricao} onChange={handleChangeEdicao} placeholder="Descrição" />
              <select name="categoria" value={equipamentoEditando.categoria} onChange={handleChangeEdicao} required>
                <option value="">Categoria</option>
                <option value="equipamento">Equipamento</option>
                <option value="periferico">Periférico</option>
              </select>
              <input type="number" name="estoque" value={equipamentoEditando.estoque} onChange={handleChangeEdicao} placeholder="Estoque" />
              <select name="status" value={equipamentoEditando.status} onChange={handleChangeEdicao} required>
                <option value="">Status</option>
                <option value="Disponível">Disponível</option>
                <option value="Em Manutenção">Em Manutenção</option>
                <option value="Indisponível">Indisponível</option>
              </select>
              <select name="fabricante" value={equipamentoEditando.fabricante} onChange={handleChangeEdicao} required>
                <option value="">Fabricante</option>
                {fabricantes.map((fab, idx) => (
                  <option key={idx} value={fab}>{fab}</option>
                ))}
              </select>
              <button type="submit">Salvar</button>
              <button type="button" onClick={() => setMostrarFormularioEdicao(false)}>Cancelar</button>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default Equipamentos;
