import React, { useState, useEffect } from 'react';
import CampoAssinatura from '../components/CampoAssinatura';

const OrdemServicoAtender = ({ match }) => {
  const [ordem, setOrdem] = useState({
    descricao_servico: '',
    estado_equipamento: '',
    nome_acompanhante: '',
    imagens_servico: [],
    status: '',
    comentarios_observacoes: [],
    novo_comentario: '',
    data_finalizacao: '',
    assinatura_tecnico: '',
    assinatura_acompanhante: ''
  });

  const [dadosOrbita, setDadosOrbita] = useState({
    numero_protocolo: '',
    nome_solicitante: '',
    telefone_solicitante: '',
    data_abertura: '',
    data_agendamento: '',
    nome_empresa: '',
    endereco: '',
    problema_reportado: '',
    tipo_visita: '',
    modalidade: '',
    nome_tecnico: ''
  });

  const ordemId = match?.params?.id || '1';
  const usuarioLogado = 'Carlos Souza';

  useEffect(() => {
    const carregarDados = async () => {
      try {
        const response = await fetch(`http://localhost:5000/api/ordem-servico/${ordemId}`);
        if (!response.ok) throw new Error('Erro ao carregar ordem');

        const data = await response.json();

        setOrdem({
          descricao_servico: data.descricao_servico || '',
          estado_equipamento: data.estado_equipamento || '',
          nome_acompanhante: data.nome_acompanhante || '',
          imagens_servico: data.imagens_servico ? JSON.parse(data.imagens_servico) : [],
          status: data.status || '',
          comentarios_observacoes: data.comentarios_observacoes ? JSON.parse(data.comentarios_observacoes) : [],
          novo_comentario: '',
          data_finalizacao: data.data_finalizacao || null,
          assinatura_tecnico: data.assinatura_tecnico || '',
          assinatura_acompanhante: data.assinatura_acompanhante || ''
        });

        setDadosOrbita({
          numero_protocolo: data.numero_protocolo,
          nome_solicitante: data.nome_solicitante,
          telefone_solicitante: data.telefone_solicitante,
          data_abertura: data.data_abertura,
          data_agendamento: data.data_agendamento,
          nome_empresa: data.nome_empresa,
          endereco: data.endereco,
          problema_reportado: data.problema_reportado,
          tipo_visita: data.tipo_visita,
          modalidade: data.modalidade,
          nome_tecnico: data.nome_tecnico
        });
      } catch (err) {
        console.error('Erro ao carregar dados da ordem:', err);
      }
    };

    carregarDados();
  }, [ordemId]);

  const autoSave = async (dadosAtualizados) => {
    try {
      await fetch(`http://localhost:5000/api/ordem-servico/${ordemId}`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(dadosAtualizados)
      });
    } catch (err) {
      console.error('Erro no auto-save:', err);
    }
  };

  const handleChange = (e) => {
    const updated = { ...ordem, [e.target.name]: e.target.value };
    if (e.target.name === 'status') {
      updated.data_finalizacao = new Date().toISOString(); // Atualiza data_finalizacao quando o status mudar
    }
    setOrdem(updated);
    autoSave(updated);
  };

  const handleAddComentario = () => {
    if (!ordem.novo_comentario.trim()) return;
    const novo = { autor: usuarioLogado, data: new Date().toISOString(), texto: ordem.novo_comentario };
    const updated = {
      ...ordem,
      comentarios_observacoes: [...ordem.comentarios_observacoes, novo],
      novo_comentario: ''
    };
    setOrdem(updated);
    autoSave(updated);
  };

  const handleImageChange = (e) => {
    const files = Array.from(e.target.files);
    const updated = { ...ordem, imagens_servico: [...ordem.imagens_servico, ...files] };
    setOrdem(updated);
    autoSave(updated);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Criando o FormData para incluir imagens
    const formData = new FormData();
    formData.append('descricao_servico', ordem.descricao_servico);
    formData.append('estado_equipamento', ordem.estado_equipamento);
    formData.append('nome_acompanhante', ordem.nome_acompanhante);
    formData.append('status', ordem.status);
    formData.append('comentarios_observacoes', JSON.stringify(ordem.comentarios_observacoes));
    formData.append('novo_comentario', ordem.novo_comentario);
    formData.append('data_finalizacao', ordem.data_finalizacao);
    formData.append('assinatura_tecnico', ordem.assinatura_tecnico);
    formData.append('assinatura_acompanhante', ordem.assinatura_acompanhante);

    // Adicionando as imagens
    ordem.imagens_servico.forEach((img, i) => {
      formData.append(`imagens_servico[${i}]`, img);
    });

    try {
      const response = await fetch(`http://localhost:5000/api/ordem-servico/${ordemId}`, {
        method: 'PUT',
        body: formData
      });

      if (response.ok) {
        alert('Ordem de serviço atualizada com sucesso!');
      } else {
        alert('Erro ao salvar a ordem de serviço.');
        console.error(await response.text());
      }
    } catch (error) {
      console.error('Erro ao enviar ordem:', error);
      alert('Erro ao conectar com o servidor.');
    }
  };

  return (
    <div className="container">
      <h2>Editar Ordem de Serviço</h2>

      <fieldset className="dados-orbita">
        <legend>Dados do Órbita App</legend>
        {Object.entries(dadosOrbita).map(([key, val]) => (
          <p key={key}>
            <strong>{key.replace(/_/g, ' ')}:</strong> {val}
          </p>
        ))}
      </fieldset>

      <form onSubmit={handleSubmit}>
        <div>
          <label>Descrição do Serviço:</label>
          <textarea name="descricao_servico" value={ordem.descricao_servico} onChange={handleChange} />
        </div>

        <div>
          <label>Estado do Equipamento:</label>
          <input type="text" name="estado_equipamento" value={ordem.estado_equipamento} onChange={handleChange} />
        </div>

        <div>
          <label>Nome do Acompanhante:</label>
          <input type="text" name="nome_acompanhante" value={ordem.nome_acompanhante} onChange={handleChange} />
        </div>

        <div>
          <label>Status:</label>
          <select name="status" value={ordem.status} onChange={handleChange}>
            {['Pendente', 'Com Pendência', 'Realizado em Validação', 'Atrasado', 'Cancelado'].map((st) => (
              <option key={st} value={st}>{st}</option>
            ))}
          </select>
        </div>

        <div>
          <label>Adicionar Imagens:</label>
          <input type="file" multiple accept="image/*" onChange={handleImageChange} />
        </div>

        {ordem.imagens_servico.length > 0 && (
          <div>
            {ordem.imagens_servico.map((img, i) => (
              <img key={i} src={URL.createObjectURL(img)} alt={`Imagem ${i + 1}`} style={{ width: '100px', margin: '5px' }} />
            ))}
          </div>
        )}

        <CampoAssinatura
          titulo="Assinatura do Técnico"
          imagemInicial={ordem.assinatura_tecnico}
          onSalvar={(imagem) => {
            const updated = { ...ordem, assinatura_tecnico: imagem };
            setOrdem(updated);
            autoSave(updated);
          }}
        />

        <CampoAssinatura
          titulo="Assinatura do Acompanhante"
          imagemInicial={ordem.assinatura_acompanhante}
          onSalvar={(imagem) => {
            const updated = { ...ordem, assinatura_acompanhante: imagem };
            setOrdem(updated);
            autoSave(updated);
          }}
        />

        <div>
          <label>Data de Finalização:</label>
          <input
            type="text"
            value={ordem.data_finalizacao ? new Date(ordem.data_finalizacao).toLocaleString() : ''}
            disabled
          />
        </div>

        <div>
          <h4>Histórico de Comentários:</h4>
          <ul>
            {ordem.comentarios_observacoes.map((c, i) => (
              <li key={i}>
                <strong>{c.autor}</strong> em {new Date(c.data).toLocaleString()}:<br />
                {c.texto}
              </li>
            ))}
          </ul>
        </div>

        <div>
          <label>Novo Comentário:</label>
          <textarea name="novo_comentario" value={ordem.novo_comentario} onChange={handleChange} />
          <button type="button" onClick={handleAddComentario}>Adicionar Comentário</button>
        </div>

        <button type="submit">Salvar</button>
      </form>
    </div>
  );
};

export default OrdemServicoAtender;
