// src/App.js
import React from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';

import Login from './pages/Login';
import RecuperarSenha from './pages/RecuperarSenha';
import RedefinirSenha from './pages/RedefinirSenha';
import Dashboard from './pages/Dashboard';
import Usuarios from './pages/Usuarios';
import OrdensServico from './pages/OrdensServico';

// Não usaremos o ProtectedRoute por enquanto, apenas em Login
function App() {
  return (
    <Router>
      <Routes>
        {/* Redireciona raiz para /login */}
        <Route path="/" element={<Navigate to="/login" replace />} />

        {/* Rotas públicas */}
        <Route path="/login" element={<Login />} />
        <Route path="/recuperar-senha" element={<RecuperarSenha />} />
        <Route path="/resetar-senha/:token" element={<RedefinirSenha />} />
        <Route path="/ordensservico" element={<OrdensServico />} /> {/* Rota sem verificação */}

        {/* Rota do Dashboard e Usuários protegidas, se necessário */}
        <Route path="/dashboard" element={<Dashboard />} /> {/* Acesso direto para o Dashboard */}
        <Route path="/usuarios" element={<Usuarios />} /> {/* Acesso direto para a página de Usuários */}

        {/* Rota coringa */}
        <Route path="*" element={<Navigate to="/login" replace />} />
      </Routes>
    </Router>
  );
}

export default App;
