// src/components/CampoAssinatura.js
import React, { useRef, useState, useEffect } from 'react';
import SignatureCanvas from 'react-signature-canvas';
import './CampoAssinatura.css'; // (Opcional) para estilos

const CampoAssinatura = ({ titulo, onSalvar, imagemInicial }) => {
  const sigCanvasRef = useRef(null);
  const [imagem, setImagem] = useState(imagemInicial || null);

  useEffect(() => {
    setImagem(imagemInicial);
  }, [imagemInicial]);

  const salvar = () => {
    if (sigCanvasRef.current && !sigCanvasRef.current.isEmpty()) {
      const imgData = sigCanvasRef.current.getTrimmedCanvas().toDataURL('image/png');
      setImagem(imgData);
      onSalvar(imgData);
    }
  };

  const limpar = () => {
    sigCanvasRef.current.clear();
    setImagem(null);
    onSalvar(null);
  };

  return (
    <div className="campo-assinatura">
      <h4>{titulo}</h4>
      <SignatureCanvas
        ref={sigCanvasRef}
        canvasProps={{ width: 300, height: 150, className: 'sigCanvas' }}
        penColor="black"
      />
      <div>
        <button type="button" onClick={salvar}>Salvar</button>
        <button type="button" onClick={limpar}>Limpar</button>
      </div>
      {imagem && (
        <div>
          <p>Assinatura salva:</p>
          <img src={imagem} alt="Assinatura" style={{ border: '1px solid #ccc' }} />
        </div>
      )}
    </div>
  );
};

export default CampoAssinatura;
