import React, { useEffect, useState } from "react";
import axios from "axios";

function Equipamentos() {
  const [equipamentos, setEquipamentos] = useState([]);
  const [fabricantes, setFabricantes] = useState([]);
  const [nome, setNome] = useState("");
  const [modelo, setModelo] = useState("");
  const [fabricanteSelecionado, setFabricanteSelecionado] = useState("");

  useEffect(() => {
    fetchEquipamentos();
    fetchFabricantes();
  }, []);

  const fetchEquipamentos = async () => {
    try {
      const response = await axios.get("http://localhost:3001/equipamentos");
      setEquipamentos(response.data);
    } catch (error) {
      console.error("Erro ao buscar equipamentos:", error);
    }
  };

  const fetchFabricantes = async () => {
    try {
      const response = await axios.get("http://localhost:3001/fabricantes");
      setFabricantes(response.data); // Deve ser um array de objetos com 'id' e 'nome'
    } catch (error) {
      console.error("Erro ao buscar fabricantes:", error);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!nome || !modelo || !fabricanteSelecionado) {
      alert("Preencha todos os campos.");
      return;
    }

    try {
      await axios.post("http://localhost:3001/equipamentos", {
        nome,
        modelo,
        fabricante: fabricanteSelecionado,
      });
      setNome("");
      setModelo("");
      setFabricanteSelecionado("");
      fetchEquipamentos();
    } catch (error) {
      console.error("Erro ao cadastrar equipamento:", error);
    }
  };

  return (
    <div>
      <h2>Cadastro de Equipamentos</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="Nome do equipamento"
          value={nome}
          onChange={(e) => setNome(e.target.value)}
        />
        <input
          type="text"
          placeholder="Modelo"
          value={modelo}
          onChange={(e) => setModelo(e.target.value)}
        />
        <select
          value={fabricanteSelecionado}
          onChange={(e) => setFabricanteSelecionado(e.target.value)}
        >
          <option value="">Selecione um fabricante</option>
          {fabricantes.map((fab) => (
            <option key={fab.id} value={fab.nome}>
              {fab.nome}
            </option>
          ))}
        </select>
        <button type="submit">Cadastrar</button>
      </form>

      <h3>Lista de Equipamentos</h3>
      <ul>
        {equipamentos.map((eq) => (
          <li key={eq.id}>
            {eq.nome} - {eq.modelo} - {eq.fabricante}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default Equipamentos;
