// src/App.js
import React from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import Login from './pages/Login';
import Dashboard from './pages/Dashboard';
import RecuperarSenha from './pages/RecuperarSenha';
import Equipamentos from './pages/Equipamentos';
import RedefinirSenha from './pages/RedefinirSenha'; // ▶️ importa aqui
import Usuarios from './pages/Usuarios';  // Importe a página de usuários
import ProtectedRoute from './components/ProtectedRoute'; // Componente de proteção de rota

function App() {
  return (
    <Router>
      <Routes>
        {/* Redireciona raiz para /login */}
        <Route path="/" element={<Navigate to="/login" replace />} />

        {/* Rotas públicas */}
        <Route path="/login" element={<Login />} />
        <Route path="/recuperar-senha" element={<RecuperarSenha />} />

        {/* Rota pública para redefinir senha via token */}
        <Route path="/resetar-senha/:token" element={<RedefinirSenha />} />

        {/* Rotas protegidas */}
        <Route
          path="/dashboard"
          element={
            <ProtectedRoute>
              <Dashboard />
            </ProtectedRoute>
          }
        />
        <Route
          path="/equipamentos"
          element={
            <ProtectedRoute>
              <Equipamentos />
            </ProtectedRoute>
          }
        />

        {/* Rota protegida para a página de usuários, apenas para admin */}
        <Route
          path="/usuarios"
          element={
            <ProtectedRoute requireAdmin={true}>
              <Usuarios />
            </ProtectedRoute>
          }
        />

        {/* Caso queira lidar com 404 */}
        <Route path="*" element={<Navigate to="/login" replace />} />
      </Routes>
    </Router>
  );
}

export default App;
