import React, { useState } from 'react';
import './styles/Equipamentos.css'; // Certifique-se de que o caminho está correto

const Equipamentos = () => {
  const [equipamentos, setEquipamentos] = useState([
    { nome: 'Equipamento 1', numeroSerie: '12345', status: 'Disponível', fabricante: 'Fabricante A' },
    { nome: 'Equipamento 2', numeroSerie: '67890', status: 'Em Manutenção', fabricante: 'Fabricante B' }
  ]);

  const [novoEquipamento, setNovoEquipamento] = useState({
    nome: '',
    numeroSerie: '',
    status: '',
    fabricante: ''
  });

  const [mostrarFormulario, setMostrarFormulario] = useState(false);  // Estado para controlar visibilidade do formulário
  const [modoEdicao, setModoEdicao] = useState(false); // Controla se é edição ou cadastro
  const [equipamentoParaEditar, setEquipamentoParaEditar] = useState(null); // Equipamento que será editado

  // Função para controlar os inputs
  const handleChange = (e) => {
    const { name, value } = e.target;
    setNovoEquipamento((prevState) => ({
      ...prevState,
      [name]: value
    }));
  };

  // Função para enviar o formulário
  const handleSubmit = (e) => {
    e.preventDefault();
    if (modoEdicao) {
      // Atualizar equipamento
      const novosEquipamentos = equipamentos.map((equipamento) =>
        equipamento === equipamentoParaEditar ? novoEquipamento : equipamento
      );
      setEquipamentos(novosEquipamentos);
    } else {
      // Adicionar novo equipamento
      setEquipamentos((prevState) => [...prevState, novoEquipamento]);
    }
    // Limpar e fechar o modal
    setNovoEquipamento({ nome: '', numeroSerie: '', status: '', fabricante: '' });
    setMostrarFormulario(false);
    setModoEdicao(false);
    setEquipamentoParaEditar(null);
  };

  // Função para abrir o modal de edição
  const handleEditar = (equipamento) => {
    setNovoEquipamento(equipamento);
    setEquipamentoParaEditar(equipamento);
    setModoEdicao(true);
    setMostrarFormulario(true);
  };

  // Função para abrir o modal de cadastro
  const handleCadastrar = () => {
    setNovoEquipamento({ nome: '', numeroSerie: '', status: '', fabricante: '' });
    setModoEdicao(false);
    setMostrarFormulario(true);
  };

  return (
    <div>
      <h2>Equipamentos</h2>
      <table>
        <thead>
          <tr>
            <th>Nome</th>
            <th>Número de Série</th>
            <th>Status</th>
            <th>Fabricante</th>
            <th>Ações</th>
          </tr>
        </thead>
        <tbody>
          {equipamentos.map((equipamento, index) => (
            <tr key={index}>
              <td>{equipamento.nome}</td>
              <td>{equipamento.numeroSerie}</td>
              <td>{equipamento.status}</td>
              <td>{equipamento.fabricante}</td>
              <td>
                <button onClick={() => handleEditar(equipamento)}>Editar</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      <button onClick={handleCadastrar}>Cadastrar Equipamento</button> {/* Mostra o formulário */}

      {mostrarFormulario && (  // Condicional para mostrar o formulário
        <div className="modal">
          <div className="modal-content">
            <span className="close" onClick={() => setMostrarFormulario(false)}>&times;</span>
            <h3>{modoEdicao ? 'Editar Equipamento' : 'Cadastrar Novo Equipamento'}</h3>
            <form onSubmit={handleSubmit}>
              <div>
                <label>Nome:</label>
                <input
                  type="text"
                  name="nome"
                  value={novoEquipamento.nome}
                  onChange={handleChange}
                  placeholder="Nome do equipamento"
                  required
                />
              </div>
              <div>
                <label>Número de Série:</label>
                <input
                  type="text"
                  name="numeroSerie"
                  value={novoEquipamento.numeroSerie}
                  onChange={handleChange}
                  placeholder="Número de série"
                  required
                />
              </div>
              <div>
                <label>Status:</label>
                <input
                  type="text"
                  name="status"
                  value={novoEquipamento.status}
                  onChange={handleChange}
                  placeholder="Status"
                  required
                />
              </div>
              <div>
                <label>Fabricante:</label>
                <input
                  type="text"
                  name="fabricante"
                  value={novoEquipamento.fabricante}
                  onChange={handleChange}
                  placeholder="Fabricante"
                  required
                />
              </div>
              <button type="submit">{modoEdicao ? 'Salvar Alterações' : 'Cadastrar Equipamento'}</button>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default Equipamentos;
