import React, { useState, useEffect } from 'react';

const OrdemServicoValidar = ({ ordemInicial }) => {
  // ordemInicial vem do pai com os dados da ordem, incluindo imagens_servico (array de URLs)

  const [ordem, setOrdem] = useState({
    descricao_servico: '',
    estado_equipamento: '',
    pecas_utilizadas: '',
    status: '',
    imagens_servico: [], // URLs das imagens já salvas no servidor
    ...ordemInicial,
  });

  // Estado para armazenar arquivos de imagens novas selecionadas
  const [imagensNovas, setImagensNovas] = useState([]);

  // Handle para inputs simples
  const handleChange = (e) => {
    const { name, value } = e.target;
    setOrdem((prev) => ({ ...prev, [name]: value }));
  };

  // Quando o técnico seleciona novas imagens (upload)
  const handleImageChange = (e) => {
    const files = Array.from(e.target.files);
    setImagensNovas((prev) => [...prev, ...files]);
  };

  // Remover uma imagem já existente (URL)
  const removerImagemExistente = (index) => {
    setOrdem((prev) => {
      const novasImagens = [...prev.imagens_servico];
      novasImagens.splice(index, 1);
      return { ...prev, imagens_servico: novasImagens };
    });
  };

  // Remover uma imagem nova (upload) antes de salvar
  const removerImagemNova = (index) => {
    setImagensNovas((prev) => {
      const novas = [...prev];
      novas.splice(index, 1);
      return novas;
    });
  };

  // Função para salvar as alterações, incluindo imagens
  const salvarAlteracoes = async () => {
    try {
      const formData = new FormData();

      formData.append('descricao_servico', ordem.descricao_servico);
      formData.append('estado_equipamento', ordem.estado_equipamento);
      formData.append('pecas_utilizadas', ordem.pecas_utilizadas);
      formData.append('status', ordem.status);

      // Passa as imagens existentes que o usuário manteve (como JSON)
      formData.append('imagens_existentes', JSON.stringify(ordem.imagens_servico));

      // Passa os arquivos das imagens novas
      imagensNovas.forEach((file) => {
        formData.append('imagens_novas', file);
      });

      const response = await fetch(`/api/ordens/${ordem.id}`, {
        method: 'PUT',
        body: formData,
      });

      if (response.ok) {
        alert('Alterações salvas com sucesso!');
        // Aqui você pode atualizar o estado, recarregar a ordem do servidor, etc.
      } else {
        alert('Erro ao salvar alterações');
      }
    } catch (error) {
      console.error(error);
      alert('Erro ao salvar alterações');
    }
  };

  return (
    <div>
      <h3>Validar Ordem de Serviço</h3>

      <div>
        <label>Descrição do Serviço:</label>
        <textarea
          name="descricao_servico"
          value={ordem.descricao_servico}
          onChange={handleChange}
          rows={4}
        />
      </div>

      <div>
        <label>Estado do Equipamento:</label>
        <input
          type="text"
          name="estado_equipamento"
          value={ordem.estado_equipamento}
          onChange={handleChange}
        />
      </div>

      <div>
        <label>Peças Utilizadas:</label>
        <input
          type="text"
          name="pecas_utilizadas"
          value={ordem.pecas_utilizadas}
          onChange={handleChange}
        />
      </div>

      <div>
        <label>Status:</label>
        <select name="status" value={ordem.status} onChange={handleChange}>
          <option value="">Selecione</option>
          <option value="Em andamento">Em andamento</option>
          <option value="Concluído">Concluído</option>
          <option value="Pendente">Pendente</option>
        </select>
      </div>

      <section className="imagens-servico">
        <h4>Imagens do Serviço</h4>

        {/* Upload de imagens novas */}
        <input
          type="file"
          multiple
          accept="image/*"
          onChange={handleImageChange}
        />

        <div style={{ display: 'flex', flexWrap: 'wrap', gap: 10, marginTop: 10 }}>
          {/* Imagens já existentes (URLs) */}
          {ordem.imagens_servico.map((url, i) => (
            <div key={`existente-${i}`} style={{ position: 'relative' }}>
              <img
                src={url}
                alt={`Imagem existente ${i + 1}`}
                style={{ width: 150, height: 150, objectFit: 'cover', borderRadius: 8 }}
              />
              <button
                onClick={() => removerImagemExistente(i)}
                style={{
                  position: 'absolute',
                  top: 5,
                  right: 5,
                  backgroundColor: 'red',
                  color: 'white',
                  border: 'none',
                  borderRadius: '50%',
                  width: 24,
                  height: 24,
                  cursor: 'pointer',
                }}
                title="Remover imagem existente"
                type="button"
              >
                ×
              </button>
            </div>
          ))}

          {/* Imagens novas (arquivos locais) */}
          {imagensNovas.map((file, i) => {
            const url = URL.createObjectURL(file);
            return (
              <div key={`nova-${i}`} style={{ position: 'relative' }}>
                <img
                  src={url}
                  alt={`Imagem nova ${i + 1}`}
                  style={{ width: 150, height: 150, objectFit: 'cover', borderRadius: 8 }}
                />
                <button
                  onClick={() => removerImagemNova(i)}
                  style={{
                    position: 'absolute',
                    top: 5,
                    right: 5,
                    backgroundColor: 'red',
                    color: 'white',
                    border: 'none',
                    borderRadius: '50%',
                    width: 24,
                    height: 24,
                    cursor: 'pointer',
                  }}
                  title="Remover imagem nova"
                  type="button"
                >
                  ×
                </button>
              </div>
            );
          })}
        </div>
      </section>

      <button onClick={salvarAlteracoes} style={{ marginTop: 20 }}>
        Salvar Alterações
      </button>
    </div>
  );
};

export default OrdemServicoValidar;
