// src/pages/Login.js
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import '../styles/Login.css'; // Importando o CSS para este componente

const Login = () => {
  const [email, setEmail]     = useState('');
  const [senha, setSenha]     = useState('');
  const [erro, setErro]       = useState('');
  const navigate              = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();

    try {
      // Faz a requisição POST para o endpoint de login do backend
      const response = await axios.post('http://localhost:5000/api/auth/login', {
        email,
        senha
      });

      // Se chegou aqui, login foi bem-sucedido
      const { token, user } = response.data;

      // Armazena o token e, se quiser, dados do usuário
      localStorage.setItem('fswToken', token);
      localStorage.setItem('fswUser', JSON.stringify(user));

      // Limpa qualquer erro anterior
      setErro('');

      // Redireciona para o dashboard
      navigate('/dashboard');
    } catch (err) {
      // Se der erro, exibe a mensagem
      const msg =
        err.response?.data?.mensagem ||
        err.response?.data?.error ||
        'Erro ao fazer login. Tente novamente.';
      setErro(msg);
    }
  };

  return (
    <div className="login-wrapper">
      <h1>FSW</h1>
      <div className="login-container">
        <h2>Login</h2>
        {erro && <p className="login-error">{erro}</p>}
        <form onSubmit={handleLogin}>
          <div>
            <label>E-mail:</label>
            <input
              type="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
            />
          </div>
          <div>
            <label>Senha:</label>
            <input
              type="password"
              value={senha}
              onChange={(e) => setSenha(e.target.value)}
              required
            />
          </div>
          <button type="submit">Entrar</button>
        </form>
        <a href="/recuperar-senha">Esqueceu a senha?</a>
      </div>
    </div>
  );
};

export default Login;
