// src/App.js
import React from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';

import Login from './pages/Login';
import RecuperarSenha from './pages/RecuperarSenha';
import RedefinirSenha from './pages/RedefinirSenha';
import Dashboard from './pages/Dashboard';
import Equipamentos from './pages/Equipamentos';
import Usuarios from './pages/Usuarios';
import Fabricantes from './pages/Fabricantes';  // Adicionei a importação da página de Fabricantes

import ProtectedRoute from './components/ProtectedRoute';

function App() {
  return (
    <Router>
      <Routes>
        {/* Redireciona raiz para /login */}
        <Route path="/" element={<Navigate to="/login" replace />} />

        {/* Rotas públicas */}
        <Route path="/login" element={<Login />} />
        <Route path="/recuperar-senha" element={<RecuperarSenha />} />

        {/* Rota pública para redefinir senha via token */}
        <Route path="/resetar-senha/:token" element={<RedefinirSenha />} />

        {/* Rotas protegidas para todos os usuários autenticados */}
        <Route
          path="/dashboard"
          element={
            <ProtectedRoute>
              <Dashboard />
            </ProtectedRoute>
          }
        />
        <Route
          path="/equipamentos"
          element={
            <ProtectedRoute>
              <Equipamentos />
            </ProtectedRoute>
          }
        />
        
        {/* Rota protegida para Fabricantes */}
        <Route
          path="/fabricantes"
          element={
            <ProtectedRoute>
              <Fabricantes />
            </ProtectedRoute>
          }
        />

        {/* Rota protegida somente para administradores */}
        <Route
          path="/usuarios"
          element={
            <ProtectedRoute requireAdmin={true}>
              <Usuarios />
            </ProtectedRoute>
          }
        />

        {/* Rota coringa: redireciona para login */}
        <Route path="*" element={<Navigate to="/login" replace />} />
      </Routes>
    </Router>
  );
}

export default App;
