// src/pages/Equipamentos.js
import '../styles/Equipamentos.css';  // Importa o CSS da página equipamentos.js
import React, { useState } from 'react';

const Equipamentos = () => {
  const [equipamentos, setEquipamentos] = useState([
    { id: 1, nome: 'Equipamento 1', numeroSerie: '12345', status: 'Disponível', fabricante: 'Fabricante A' },
    { id: 2, nome: 'Equipamento 2', numeroSerie: '67890', status: 'Em Manutenção', fabricante: 'Fabricante B' }
  ]);

  const [novoEquipamento, setNovoEquipamento] = useState({
    nome: '',
    numeroSerie: '',
    status: '',
    fabricante: ''
  });

  const [equipamentoEditando, setEquipamentoEditando] = useState(null); // Controle do equipamento sendo editado
  const [mostrarFormulario, setMostrarFormulario] = useState(false); // Controle do modal de cadastro
  const [mostrarFormularioEdicao, setMostrarFormularioEdicao] = useState(false); // Controle do modal de edição

  // Função para lidar com mudanças nos inputs do formulário de cadastro
  const handleChange = (e) => {
    const { name, value } = e.target;
    setNovoEquipamento((prevState) => ({
      ...prevState,
      [name]: value
    }));
  };

  // Função para lidar com mudanças nos inputs do formulário de edição
  const handleChangeEdicao = (e) => {
    const { name, value } = e.target;
    setEquipamentoEditando((prevState) => ({
      ...prevState,
      [name]: value
    }));
  };

  // Função para cadastrar novo equipamento
  const handleSubmitCadastro = (e) => {
    e.preventDefault();
    if (novoEquipamento.nome && novoEquipamento.numeroSerie) {  // Verificar se os campos obrigatórios estão preenchidos
      setEquipamentos((prevState) => [...prevState, { ...novoEquipamento, id: Date.now() }]);
      setNovoEquipamento({ nome: '', numeroSerie: '', status: '', fabricante: '' });
      setMostrarFormulario(false);  // Fechar o modal de cadastro
    }
  };

  // Função para salvar as alterações no equipamento editado
  const handleSubmitEdicao = (e) => {
    e.preventDefault();
    if (equipamentoEditando.nome && equipamentoEditando.numeroSerie) {  // Verificar se os campos obrigatórios estão preenchidos
      setEquipamentos((prevState) =>
        prevState.map((equipamento) =>
          equipamento.id === equipamentoEditando.id ? equipamentoEditando : equipamento
        )
      );
      setEquipamentoEditando(null); // Limpar o estado de edição
      setMostrarFormularioEdicao(false);  // Fechar o modal de edição
    }
  };

  // Função para abrir o modal de edição
  const handleEditClick = (equipamento) => {
    setEquipamentoEditando(equipamento);
    setMostrarFormularioEdicao(true);
  };

  return (
    <div>
      <h2>Equipamentos</h2>

      {/* Tabela de equipamentos */}
      <table>
        <thead>
          <tr>
            <th>Nome</th>
            <th>Número de Série</th>
            <th>Status</th>
            <th>Fabricante</th>
            <th>Ações</th>
          </tr>
        </thead>
        <tbody>
          {equipamentos.map((equipamento) => (
            <tr key={equipamento.id}>
              <td>{equipamento.nome}</td>
              <td>{equipamento.numeroSerie}</td>
              <td>{equipamento.status}</td>
              <td>{equipamento.fabricante}</td>
              <td>
                <button onClick={() => handleEditClick(equipamento)}>Editar</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {/* Botão para abrir o modal de cadastro de novo equipamento */}
      <button onClick={() => setMostrarFormulario(true)}>Cadastrar Equipamento</button>

      {/* Modal de Cadastro de Equipamento */}
      {mostrarFormulario && (
        <div className="modal">
          <div className="modal-content">
            <h3>Cadastrar Novo Equipamento</h3>
            <form onSubmit={handleSubmitCadastro}>
              <div>
                <label>Nome:</label>
                <input
                  type="text"
                  name="nome"
                  value={novoEquipamento.nome}
                  onChange={handleChange}
                  placeholder="Nome do equipamento"
                  required
                />
              </div>
              <div>
                <label>Número de Série:</label>
                <input
                  type="text"
                  name="numeroSerie"
                  value={novoEquipamento.numeroSerie}
                  onChange={handleChange}
                  placeholder="Número de série"
                  required
                />
              </div>
              <div>
                <label>Status:</label>
                <input
                  type="text"
                  name="status"
                  value={novoEquipamento.status}
                  onChange={handleChange}
                  placeholder="Status"
                  required
                />
              </div>
              <div>
                <label>Fabricante:</label>
                <input
                  type="text"
                  name="fabricante"
                  value={novoEquipamento.fabricante}
                  onChange={handleChange}
                  placeholder="Fabricante"
                  required
                />
              </div>
              <button type="submit">Cadastrar Equipamento</button>
              <button type="button" onClick={() => setMostrarFormulario(false)}>
                Cancelar
              </button>
            </form>
          </div>
        </div>
      )}

      {/* Modal de Edição de Equipamento */}
      {mostrarFormularioEdicao && equipamentoEditando && (
        <div className="modal">
          <div className="modal-content">
            <h3>Editar Equipamento</h3>
            <form onSubmit={handleSubmitEdicao}>
              <div>
                <label>Nome:</label>
                <input
                  type="text"
                  name="nome"
                  value={equipamentoEditando.nome}
                  onChange={handleChangeEdicao}
                  placeholder="Nome do equipamento"
                  required
                />
              </div>
              <div>
                <label>Número de Série:</label>
                <input
                  type="text"
                  name="numeroSerie"
                  value={equipamentoEditando.numeroSerie}
                  onChange={handleChangeEdicao}
                  placeholder="Número de série"
                  required
                />
              </div>
              <div>
                <label>Status:</label>
                <input
                  type="text"
                  name="status"
                  value={equipamentoEditando.status}
                  onChange={handleChangeEdicao}
                  placeholder="Status"
                  required
                />
              </div>
              <div>
                <label>Fabricante:</label>
                <input
                  type="text"
                  name="fabricante"
                  value={equipamentoEditando.fabricante}
                  onChange={handleChangeEdicao}
                  placeholder="Fabricante"
                  required
                />
              </div>
              <button type="submit">Salvar Alterações</button>
              <button type="button" onClick={() => setMostrarFormularioEdicao(false)}>
                Cancelar
              </button>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default Equipamentos;
