// src/pages/Fabricantes.js
import React, { useState, useEffect } from 'react';
import './Fabricantes.css'; // Importando o arquivo de estilo

const Fabricantes = () => {
  const [fabricantes, setFabricantes] = useState([]);
  const [showPopup, setShowPopup] = useState(false); // Controle da exibição do popup
  const [nome, setNome] = useState('');
  const [nomeVendedor, setNomeVendedor] = useState('');
  const [contatoVendedor, setContatoVendedor] = useState('');

  // Função para buscar os fabricantes (substitua com a lógica real de busca)
  const fetchFabricantes = async () => {
    // Aqui você pode substituir o mock por uma chamada API real
    setFabricantes([
      { id: 1, nome: 'Fabricante A' },
      { id: 2, nome: 'Fabricante B' },
    ]);
  };

  // Efeito para buscar os fabricantes assim que o componente for montado
  useEffect(() => {
    fetchFabricantes();
  }, []);

  // Função de cadastro de fabricante (simulando com log)
  const handleCadastrar = () => {
    // Aqui você pode substituir com a lógica de cadastro real (API, etc.)
    console.log('Cadastrando:', nome, nomeVendedor, contatoVendedor);
    setShowPopup(false); // Fecha o popup após o cadastro
  };

  return (
    <div>
      <h2>Fabricantes</h2>

      {/* Botão para abrir o popup de cadastro */}
      <button onClick={() => setShowPopup(true)}>Cadastrar Fabricante</button>

      {/* Lista de fabricantes */}
      <ul>
        {fabricantes.map((fabricante) => (
          <li key={fabricante.id}>{fabricante.nome}</li>
        ))}
      </ul>

      {/* Popup de cadastro */}
      {showPopup && (
        <div className="popup">
          <div className="popup-content">
            <h3>Cadastrar Fabricante</h3>
            <form onSubmit={(e) => e.preventDefault()}>
              <div>
                <label>Nome do Fabricante:</label>
                <input
                  type="text"
                  value={nome}
                  onChange={(e) => setNome(e.target.value)}
                  required
                />
              </div>
              <div>
                <label>Nome do Vendedor:</label>
                <input
                  type="text"
                  value={nomeVendedor}
                  onChange={(e) => setNomeVendedor(e.target.value)}
                />
              </div>
              <div>
                <label>Contato do Vendedor:</label>
                <input
                  type="text"
                  value={contatoVendedor}
                  onChange={(e) => setContatoVendedor(e.target.value)}
                />
              </div>
              <button type="button" onClick={handleCadastrar}>
                Cadastrar
              </button>
              <button type="button" onClick={() => setShowPopup(false)}>
                Cancelar
              </button>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default Fabricantes;
