import React, { useState, useEffect } from 'react';

const OrdensServico = () => {
  const [ordens, setOrdens] = useState([]);

  useEffect(() => {
    // Depois conectamos com o back-end
    // Por enquanto, dados mockados:
    setOrdens([
      {
        id: 1,
        equipamento: 'Bomba de Infusão',
        cliente: 'Hospital Santa Clara',
        status: 'Aberta',
        data: '2025-05-05'
      },
      {
        id: 2,
        equipamento: 'Desfibrilador',
        cliente: 'Clínica Vida',
        status: 'Concluída',
        data: '2025-05-02'
      }
    ]);
  }, []);

  return (
    <div style={{ padding: '20px' }}>
      <h2>Ordens de Serviço</h2>
      <button onClick={() => alert('Abrir formulário de nova OS')}>Nova Ordem de Serviço</button>

      <table border="1" cellPadding="8" style={{ marginTop: '20px', width: '100%' }}>
        <thead>
          <tr>
            <th>ID</th>
            <th>Equipamento</th>
            <th>Cliente</th>
            <th>Status</th>
            <th>Data</th>
            <th>Ações</th>
          </tr>
        </thead>
        <tbody>
          {ordens.map(ordem => (
            <tr key={ordem.id}>
              <td>{ordem.id}</td>
              <td>{ordem.equipamento}</td>
              <td>{ordem.cliente}</td>
              <td>{ordem.status}</td>
              <td>{ordem.data}</td>
              <td>
                <button onClick={() => alert(`Visualizar OS ${ordem.id}`)}>Ver</button>
                <button onClick={() => alert(`Editar OS ${ordem.id}`)}>Editar</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default OrdensServico;
