import React, { useState } from 'react';
import axios from 'axios';
import { useParams, useNavigate } from 'react-router-dom';
import '../styles/RedefinirSenha.css'; // Adicionar o estilo conforme necessário

function RedefinirSenha() {
  const { token } = useParams();        // Pega o token da URL
  const [newPassword, setNewPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [message, setMessage] = useState('');
  const [loading, setLoading] = useState(false);  // Indicador de carregamento
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (newPassword !== confirmPassword) {
      setMessage('As senhas não coincidem.');
      return;
    }

    setLoading(true);
    try {
      const response = await axios.post('http://localhost:5000/api/resetar-senha', {
        token,
        newPassword
      });

      if (response.status === 200) {
        setMessage('Senha redefinida com sucesso! Redirecionando ao login...');
        setTimeout(() => navigate('/login'), 2000);
      }
    } catch (error) {
      setMessage('Erro ao redefinir a senha. Verifique o token ou tente novamente.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="reset-wrapper">
      <h2>Redefinir Senha</h2>
      <form onSubmit={handleSubmit} className="reset-form">
        <label htmlFor="newPassword">Nova Senha:</label>
        <input
          id="newPassword"
          type="password"
          value={newPassword}
          onChange={e => setNewPassword(e.target.value)}
          required
        />
        <label htmlFor="confirmPassword">Confirmar Senha:</label>
        <input
          id="confirmPassword"
          type="password"
          value={confirmPassword}
          onChange={e => setConfirmPassword(e.target.value)}
          required
        />
        <button type="submit" disabled={loading}>
          {loading ? 'Redefinindo...' : 'Redefinir'}
        </button>
      </form>
      {message && <p className="reset-message">{message}</p>}
    </div>
  );
}

export default RedefinirSenha;
