// src/pages/Equipamentos.js
import '../styles/Equipamentos.css';  // Importa o CSS da página equipamentos.js
import React, { useState } from 'react';

const Equipamentos = () => {
  const [fabricantes] = useState(['Fabricante A', 'Fabricante B', 'Fabricante C']); // Lista fixa de fabricantes

  const [equipamentos, setEquipamentos] = useState([
    { id: 1, nome: 'Equipamento 1', status: 'Disponível', fabricante: 'Fabricante A' },
    { id: 2, nome: 'Equipamento 2', status: 'Em Manutenção', fabricante: 'Fabricante B' }
  ]);

  const [novoEquipamento, setNovoEquipamento] = useState({
    nome: '',
    status: '',
    fabricante: ''
  });

  const [equipamentoEditando, setEquipamentoEditando] = useState(null);
  const [mostrarFormulario, setMostrarFormulario] = useState(false);
  const [mostrarFormularioEdicao, setMostrarFormularioEdicao] = useState(false);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setNovoEquipamento((prevState) => ({
      ...prevState,
      [name]: value
    }));
  };

  const handleChangeEdicao = (e) => {
    const { name, value } = e.target;
    setEquipamentoEditando((prevState) => ({
      ...prevState,
      [name]: value
    }));
  };

  const handleSubmitCadastro = (e) => {
    e.preventDefault();
    if (novoEquipamento.nome && novoEquipamento.status && novoEquipamento.fabricante) {
      setEquipamentos((prevState) => [...prevState, { ...novoEquipamento, id: Date.now() }]);
      setNovoEquipamento({ nome: '', status: '', fabricante: '' });
      setMostrarFormulario(false);
    }
  };

  const handleSubmitEdicao = (e) => {
    e.preventDefault();
    if (equipamentoEditando.nome && equipamentoEditando.status && equipamentoEditando.fabricante) {
      setEquipamentos((prevState) =>
        prevState.map((equipamento) =>
          equipamento.id === equipamentoEditando.id ? equipamentoEditando : equipamento
        )
      );
      setEquipamentoEditando(null);
      setMostrarFormularioEdicao(false);
    }
  };

  const handleEditClick = (equipamento) => {
    setEquipamentoEditando(equipamento);
    setMostrarFormularioEdicao(true);
  };

  return (
    <div>
      <h2>Equipamentos</h2>

      {/* Tabela de equipamentos */}
      <table>
        <thead>
          <tr>
            <th>Nome</th>
            <th>Status</th>
            <th>Fabricante</th>
            <th>Ações</th>
          </tr>
        </thead>
        <tbody>
          {equipamentos.map((equipamento) => (
            <tr key={equipamento.id}>
              <td>{equipamento.nome}</td>
              <td>{equipamento.status}</td>
              <td>{equipamento.fabricante}</td>
              <td>
                <button onClick={() => handleEditClick(equipamento)}>Editar</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {/* Botão para abrir o modal de cadastro de novo equipamento */}
      <button onClick={() => setMostrarFormulario(true)}>Cadastrar Equipamento</button>

      {/* Modal de Cadastro de Equipamento */}
      {mostrarFormulario && (
        <div className="modal">
          <div className="modal-content">
            <h3>Cadastrar Novo Equipamento</h3>
            <form onSubmit={handleSubmitCadastro}>
              <div>
                <label>Nome:</label>
                <input
                  type="text"
                  name="nome"
                  value={novoEquipamento.nome}
                  onChange={handleChange}
                  placeholder="Nome do equipamento"
                  required
                />
              </div>
              <div>
                <label>Status:</label>
                <select
                  name="status"
                  value={novoEquipamento.status}
                  onChange={handleChange}
                  required
                >
                  <option value="">Selecione o status</option>
                  <option value="Disponível">Disponível</option>
                  <option value="Em Manutenção">Em Manutenção</option>
                  <option value="Indisponível">Indisponível</option>
                </select>
              </div>
              <div>
                <label>Fabricante:</label>
                <select
                  name="fabricante"
                  value={novoEquipamento.fabricante}
                  onChange={handleChange}
                  required
                >
                  <option value="">Selecione o fabricante</option>
                  {fabricantes.map((fab, index) => (
                    <option key={index} value={fab}>{fab}</option>
                  ))}
                </select>
              </div>
              <button type="submit">Cadastrar Equipamento</button>
              <button type="button" onClick={() => setMostrarFormulario(false)}>
                Cancelar
              </button>
            </form>
          </div>
        </div>
      )}

      {/* Modal de Edição de Equipamento */}
      {mostrarFormularioEdicao && equipamentoEditando && (
        <div className="modal">
          <div className="modal-content">
            <h3>Editar Equipamento</h3>
            <form onSubmit={handleSubmitEdicao}>
              <div>
                <label>Nome:</label>
                <input
                  type="text"
                  name="nome"
                  value={equipamentoEditando.nome}
                  onChange={handleChangeEdicao}
                  placeholder="Nome do equipamento"
                  required
                />
              </div>
              <div>
                <label>Status:</label>
                <select
                  name="status"
                  value={equipamentoEditando.status}
                  onChange={handleChangeEdicao}
                  required
                >
                  <option value="">Selecione o status</option>
                  <option value="Disponível">Disponível</option>
                  <option value="Em Manutenção">Em Manutenção</option>
                  <option value="Indisponível">Indisponível</option>
                </select>
              </div>
              <div>
                <label>Fabricante:</label>
                <select
                  name="fabricante"
                  value={equipamentoEditando.fabricante}
                  onChange={handleChangeEdicao}
                  required
                >
                  <option value="">Selecione o fabricante</option>
                  {fabricantes.map((fab, index) => (
                    <option key={index} value={fab}>{fab}</option>
                  ))}
                </select>
              </div>
              <button type="submit">Salvar Alterações</button>
              <button type="button" onClick={() => setMostrarFormularioEdicao(false)}>
                Cancelar
              </button>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default Equipamentos;
