// src/pages/RedefinirSenha.js
import React, { useState } from 'react';
import axios from 'axios';
import { useParams, useNavigate } from 'react-router-dom';
import '../styles/RedefinirSenha.css'; // depois criaremos esse CSS

function RedefinirSenha() {
  const { token } = useParams();        // pega o token da URL
  const [newPassword, setNewPassword] = useState('');
  const [message, setMessage]       = useState('');
  const navigate                    = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:5000/api/resetar-senha', {
        token,
        newPassword
      });
      setMessage('Senha redefinida com sucesso! Redirecionando ao login...');
      setTimeout(() => navigate('/login'), 2000);
    } catch (error) {
      setMessage('Erro ao redefinir a senha. Tente novamente.');
    }
  };

  return (
    <div className="reset-wrapper">
      <h2>Redefinir Senha</h2>
      <form onSubmit={handleSubmit} className="reset-form">
        <label htmlFor="newPassword">Nova Senha:</label>
        <input
          id="newPassword"
          type="password"
          value={newPassword}
          onChange={e => setNewPassword(e.target.value)}
          required
        />
        <button type="submit">Redefinir</button>
      </form>
      {message && <p className="reset-message">{message}</p>}
    </div>
  );
}

export default RedefinirSenha;
