import React, { useState, useEffect, useRef } from 'react';
import SignatureCanvas from 'react-signature-canvas'; // Importando diretamente

const OrdemServicoAtender = ({ match }) => {
  const [ordem, setOrdem] = useState({
    descricao_servico: '',
    estado_equipamento: '',
    nome_acompanhante: '',
    assinatura_acompanhante: '',
    assinatura_tecnico: '',
    imagens_servico: [],
    status: '',
    comentarios_observacoes: [],
    novo_comentario: '',
    data_finalizacao: ''
  });

  const [dadosOrbita, setDadosOrbita] = useState({
    numero_protocolo: '',
    nome_solicitante: '',
    telefone_solicitante: '',
    data_abertura: '',
    data_agendamento: '',
    nome_empresa: '',
    endereco: '',
    problema_reportado: '',
    tipo_visita: '',
    modalidade: '',
    nome_tecnico: ''
  });

  const sigCanvasTecnico = useRef(null);
  const sigCanvasAcompanhante = useRef(null);

  const ordemId = match?.params?.id || '1';
  const usuarioLogado = 'Carlos Souza';

  useEffect(() => {
    const dadosSalvos = localStorage.getItem(`ordemServico-${ordemId}`);
    if (dadosSalvos) {
      setOrdem(JSON.parse(dadosSalvos));
    }

    const dadosOrbitaSalvos = localStorage.getItem(`orbita-${ordemId}`);
    if (dadosOrbitaSalvos) {
      setDadosOrbita(JSON.parse(dadosOrbitaSalvos));
    }
  }, [ordemId]);

  const handleChange = (e) => {
    const novaData = new Date().toISOString();
    const updatedOrdem = {
      ...ordem,
      [e.target.name]: e.target.value,
      data_finalizacao: novaData
    };
    setOrdem(updatedOrdem);
    localStorage.setItem(`ordemServico-${ordemId}`, JSON.stringify(updatedOrdem));
  };

  const handleAddComentario = () => {
    if (ordem.novo_comentario.trim() !== '') {
      const novo = {
        autor: usuarioLogado,
        data: new Date().toISOString(),
        texto: ordem.novo_comentario
      };
      const updatedOrdem = {
        ...ordem,
        comentarios_observacoes: [...ordem.comentarios_observacoes, novo],
        novo_comentario: ''
      };
      setOrdem(updatedOrdem);
      localStorage.setItem(`ordemServico-${ordemId}`, JSON.stringify(updatedOrdem));
    }
  };

  const handleImageChange = (e) => {
    const files = e.target.files;
    const newImages = Array.from(files).map((file) => URL.createObjectURL(file));
    const updatedOrdem = {
      ...ordem,
      imagens_servico: [...ordem.imagens_servico, ...newImages]
    };
    setOrdem(updatedOrdem);
    localStorage.setItem(`ordemServico-${ordemId}`, JSON.stringify(updatedOrdem));
  };

  const salvarAssinatura = (tipo) => {
    let dataUrl;
    if (tipo === 'tecnico' && sigCanvasTecnico.current?.getTrimmedCanvas) {
      dataUrl = sigCanvasTecnico.current.getTrimmedCanvas().toDataURL('image/png');
      const updated = { ...ordem, assinatura_tecnico: dataUrl };
      setOrdem(updated);
      localStorage.setItem(`ordemServico-${ordemId}`, JSON.stringify(updated));
    } else if (tipo === 'acompanhante' && sigCanvasAcompanhante.current?.getTrimmedCanvas) {
      dataUrl = sigCanvasAcompanhante.current.getTrimmedCanvas().toDataURL('image/png');
      const updated = { ...ordem, assinatura_acompanhante: dataUrl };
      setOrdem(updated);
      localStorage.setItem(`ordemServico-${ordemId}`, JSON.stringify(updated));
    }
  };

  const limparAssinatura = (tipo) => {
    if (tipo === 'tecnico' && sigCanvasTecnico.current?.clear) {
      sigCanvasTecnico.current.clear();
      setOrdem((prev) => ({ ...prev, assinatura_tecnico: '' }));
    } else if (tipo === 'acompanhante' && sigCanvasAcompanhante.current?.clear) {
      sigCanvasAcompanhante.current.clear();
      setOrdem((prev) => ({ ...prev, assinatura_acompanhante: '' }));
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Ordem de serviço salva:', ordem);
    alert('Ordem de serviço atualizada com sucesso!');
  };

  return (
    <div className="container">
      <h2>Editar Ordem de Serviço</h2>

      <fieldset className="dados-orbita">
        <legend>Dados do Órbita App</legend>
        {Object.entries(dadosOrbita).map(([key, value]) => (
          <p key={key}><strong>{key.replace(/_/g, ' ')}:</strong> {value}</p>
        ))}
      </fieldset>

      <form onSubmit={handleSubmit}>
        <div>
          <label>Descrição do Serviço:</label>
          <textarea name="descricao_servico" value={ordem.descricao_servico} onChange={handleChange} />
        </div>

        <div>
          <label>Estado do Equipamento:</label>
          <input type="text" name="estado_equipamento" value={ordem.estado_equipamento} onChange={handleChange} />
        </div>

        <div>
          <label>Nome do Acompanhante:</label>
          <input type="text" name="nome_acompanhante" value={ordem.nome_acompanhante} onChange={handleChange} />
        </div>

        <div>
          <label>Assinatura do Acompanhante:</label><br />
          <SignatureCanvas
            ref={sigCanvasAcompanhante}
            penColor="black"
            canvasProps={{ width: 300, height: 150, className: 'sigCanvas' }}
            onEnd={() => salvarAssinatura('acompanhante')}
          />
          <button type="button" onClick={() => limparAssinatura('acompanhante')}>Limpar Assinatura</button>
          {ordem.assinatura_acompanhante && <img src={ordem.assinatura_acompanhante} alt="Assinatura Acompanhante" />}
        </div>

        <div>
          <label>Assinatura do Técnico:</label><br />
          <SignatureCanvas
            ref={sigCanvasTecnico}
            penColor="black"
            canvasProps={{ width: 300, height: 150, className: 'sigCanvas' }}
            onEnd={() => salvarAssinatura('tecnico')}
          />
          <button type="button" onClick={() => limparAssinatura('tecnico')}>Limpar Assinatura</button>
          {ordem.assinatura_tecnico && <img src={ordem.assinatura_tecnico} alt="Assinatura Técnico" />}
        </div>

        <div>
          <label>Status:</label>
          <select name="status" value={ordem.status} onChange={handleChange}>
            {['Pendente', 'Com Pendência', 'Realizado em Validação', 'Atrasado', 'Cancelado'].map((status) => (
              <option key={status} value={status}>{status}</option>
            ))}
          </select>
        </div>

        <div>
          <label>Adicionar Imagens:</label>
          <input type="file" multiple accept="image/*" onChange={handleImageChange} />
        </div>

        {ordem.imagens_servico.length > 0 && (
          <div>
            {ordem.imagens_servico.map((img, index) => (
              <img key={index} src={img} alt={`Imagem ${index + 1}`} />
            ))}
          </div>
        )}

        <div>
          <label>Novo Comentário:</label>
          <textarea name="novo_comentario" value={ordem.novo_comentario} onChange={handleChange} />
          <button type="button" onClick={handleAddComentario}>Adicionar Comentário</button>
        </div>

        <div>
          <h4>Histórico de Comentários:</h4>
          <ul>
            {ordem.comentarios_observacoes.map((coment, idx) => (
              <li key={idx}>
                <strong>{coment.autor}</strong> em {new Date(coment.data).toLocaleString()}<br />
                {coment.texto}
              </li>
            ))}
          </ul>
        </div>

        <div>
          <label>Data de Finalização:</label>
          <input type="text" value={ordem.data_finalizacao ? new Date(ordem.data_finalizacao).toLocaleString() : ''} disabled />
        </div>

        <button type="submit">Salvar</button>
      </form>
    </div>
  );
};

export default OrdemServicoAtender;
