import React, { useState, useEffect } from 'react';

const OrdemServicoEditar = ({ match }) => {
  const [ordem, setOrdem] = useState({
    descricao_servico: '',
    estado_equipamento: '',
    nome_acompanhante: '',
    assinatura_acompanhante: '',
    assinatura_tecnico: '',
    imagens_servico: [],
    data_finalizacao: '',
    status: '',
    comentarios_observacoes: [],
    novo_comentario: ''
  });

  const [dadosOrbita, setDadosOrbita] = useState({
    numero_protocolo: '',
    nome_solicitante: '',
    telefone_solicitante: '',
    data_abertura: '',
    data_agendamento: '',
    nome_empresa: '',
    endereco: '',
    problema_reportado: '',
    tipo_visita: '',
    modalidade: '',
    nome_tecnico: ''
  });

  const ordemId = match?.params?.id || '1'; // Ajuste caso não esteja usando rota com ID
  const usuarioLogado = 'Carlos Souza'; // Exemplo de usuário logado

  useEffect(() => {
    // Simula busca dos dados no banco
    const dadosMockOrbita = {
      numero_protocolo: 'PROTO123',
      nome_solicitante: 'Maria Oliveira',
      telefone_solicitante: '11999999999',
      data_abertura: '2025-05-01',
      data_agendamento: '2025-05-06',
      nome_empresa: 'Hospital Vida',
      endereco: 'Rua das Flores, 456',
      problema_reportado: 'Monitor não liga',
      tipo_visita: 'Técnico',
      modalidade: 'Presencial',
      nome_tecnico: 'Carlos Souza'
    };
    setDadosOrbita(dadosMockOrbita);
  }, [ordemId]);

  const handleChange = (e) => {
    setOrdem({ ...ordem, [e.target.name]: e.target.value });
  };

  const handleAddComentario = () => {
    if (ordem.novo_comentario.trim() !== '') {
      const novo = {
        autor: usuarioLogado,
        data: new Date().toISOString(),
        texto: ordem.novo_comentario
      };
      setOrdem({
        ...ordem,
        comentarios_observacoes: [...ordem.comentarios_observacoes, novo],
        novo_comentario: ''
      });
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Ordem de serviço antes de salvar:', ordem);

    if (ordem.status === 'Finalizado' && !ordem.assinatura_tecnico) {
      alert('A assinatura do técnico é obrigatória para finalizar a ordem de serviço.');
      return;
    }

    console.log('Ordem atualizada:', ordem);
    alert('Ordem de serviço atualizada com sucesso!');
  };

  return (
    <div style={{ padding: '20px' }}>
      <h2>Editar Ordem de Serviço</h2>

      {/* Exibe dados do Órbita App */}
      <fieldset disabled={ordem.status === 'Finalizado'} style={{ background: '#f2f2f2', padding: '10px' }}>
        <legend>Dados do Órbita App</legend>
        <p><strong>Protocolo:</strong> {dadosOrbita.numero_protocolo}</p>
        <p><strong>Solicitante:</strong> {dadosOrbita.nome_solicitante}</p>
        <p><strong>Telefone:</strong> {dadosOrbita.telefone_solicitante}</p>
        <p><strong>Data de Abertura:</strong> {dadosOrbita.data_abertura}</p>
        <p><strong>Data de Agendamento:</strong> {dadosOrbita.data_agendamento}</p>
        <p><strong>Empresa:</strong> {dadosOrbita.nome_empresa}</p>
        <p><strong>Endereço:</strong> {dadosOrbita.endereco}</p>
        <p><strong>Problema Reportado:</strong> {dadosOrbita.problema_reportado}</p>
        <p><strong>Tipo de Visita:</strong> {dadosOrbita.tipo_visita}</p>
        <p><strong>Modalidade:</strong> {dadosOrbita.modalidade}</p>
        <p><strong>Técnico:</strong> {dadosOrbita.nome_tecnico}</p>
      </fieldset>

      {/* Formulário de edição */}
      <form onSubmit={handleSubmit}>
        <div>
          <label>Descrição do Serviço:</label>
          <textarea name="descricao_servico" value={ordem.descricao_servico} onChange={handleChange} />
        </div>

        <div>
          <label>Estado do Equipamento:</label>
          <input type="text" name="estado_equipamento" value={ordem.estado_equipamento} onChange={handleChange} />
        </div>

        <div>
          <label>Nome do Acompanhante:</label>
          <input type="text" name="nome_acompanhante" value={ordem.nome_acompanhante} onChange={handleChange} />
        </div>

        <div>
          <label>Assinatura Acompanhante (Base64):</label>
          <input type="text" name="assinatura_acompanhante" value={ordem.assinatura_acompanhante} onChange={handleChange} />
        </div>

        <div>
          <label>Assinatura Técnico (Base64):</label>
          <input type="text" name="assinatura_tecnico" value={ordem.assinatura_tecnico} onChange={handleChange} />
        </div>

        <div>
          <label>Data Finalização:</label>
          <input type="date" name="data_finalizacao" value={ordem.data_finalizacao} onChange={handleChange} />
        </div>

        <div>
          <label>Status:</label>
          <select name="status" value={ordem.status} onChange={handleChange}>
            <option value="">Selecione</option>
            <option value="Aguardando Atendimento">Aguardando Atendimento</option>
            <option value="Em Pendência">Em Pendência</option>
            <option value="Realizado Em Validação">Realizado Em Validação</option>
            <option value="Finalizado">Finalizado</option>
            <option value="Atrasado">Atrasado</option>
          </select>
        </div>

        {/* Comentários */}
        <div>
          <label>Novo Comentário:</label>
          <textarea name="novo_comentario" value={ordem.novo_comentario} onChange={handleChange} />
          <button type="button" onClick={handleAddComentario}>Adicionar Comentário</button>
        </div>

        <div>
          <h4>Histórico de Comentários:</h4>
          <ul>
            {ordem.comentarios_observacoes.map((coment, idx) => (
              <li key={idx}>
                <strong>{coment.autor}</strong> em {new Date(coment.data).toLocaleString()}<br />
                {coment.texto}
              </li>
            ))}
          </ul>
        </div>

        <button type="submit">Salvar</button>
      </form>
    </div>
  );
};

export default OrdemServicoEditar;
