import React, { useEffect, useState } from 'react';
import axios from 'axios';  // Importando axios para realizar requisição HTTP

const Dashboard = () => {
  const [ordens, setOrdens] = useState([]);  // Estado para armazenar as ordens de serviço

  useEffect(() => {
    // Função para buscar as ordens de serviço do backend
    const fetchOrdens = async () => {
      try {
        // Fazendo a requisição para o backend
        const response = await axios.get('http://localhost:5000/api/ordens-de-servico');
        
        // Atualizando o estado com os dados retornados da API
        setOrdens(response.data);
      } catch (error) {
        console.error('Erro ao buscar ordens de serviço:', error);
      }
    };

    fetchOrdens();
  }, []);  // O array vazio [] faz com que a requisição seja feita apenas uma vez quando o componente for montado

  return (
    <div className="dashboard-container">
      <h2>Dashboard - Ordens de Serviço</h2>
      <table>
        <thead>
          <tr>
            <th>Protocolo</th>
            <th>Solicitante</th>
            <th>Problema Reportado</th>
            <th>Status</th>
            <th>Ações</th>
          </tr>
        </thead>
        <tbody>
          {ordens.map((ordem) => (
            <tr key={ordem.id}>
              <td>{ordem.numero_protocolo}</td>
              <td>{ordem.nome_solicitante}</td>
              <td>{ordem.problema_reportado}</td>
              <td>{ordem.status}</td>
              <td>
                <button>Validar</button>
                <button>Atender</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Dashboard;
