// src/pages/Equipamentos.js
import '../styles/Equipamentos.css';
import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Equipamentos = () => {
  const [fabricantes, setFabricantes] = useState(['Fabricante A', 'Fabricante B', 'Fabricante C']);
  const [equipamentos, setEquipamentos] = useState([]);
  const [novoEquipamento, setNovoEquipamento] = useState({ nome: '', status: '', fabricante: '' });
  const [equipamentoEditando, setEquipamentoEditando] = useState(null);
  const [mostrarFormulario, setMostrarFormulario] = useState(false);
  const [mostrarFormularioEdicao, setMostrarFormularioEdicao] = useState(false);

  const userId = localStorage.getItem('userId'); // <-- ID do usuÃ¡rio logado

  useEffect(() => {
    const fetchEquipamentos = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/equipamentos');
        setEquipamentos(response.data);
      } catch (error) {
        console.error('Erro ao buscar equipamentos', error);
      }
    };
    fetchEquipamentos();
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setNovoEquipamento((prev) => ({ ...prev, [name]: value }));
  };

  const handleChangeEdicao = (e) => {
    const { name, value } = e.target;
    setEquipamentoEditando((prev) => ({ ...prev, [name]: value }));
  };

  const handleSubmitCadastro = async (e) => {
    e.preventDefault();
    if (novoEquipamento.nome && novoEquipamento.status && novoEquipamento.fabricante) {
      try {
        const response = await axios.post('http://localhost:5000/api/equipamentos', {
          ...novoEquipamento,
          criado_por: userId // <-- Quem criou
        });
        setEquipamentos((prev) => [...prev, response.data]);
        setNovoEquipamento({ nome: '', status: '', fabricante: '' });
        setMostrarFormulario(false);
      } catch (error) {
        console.error('Erro ao cadastrar equipamento', error);
      }
    }
  };

  const handleSubmitEdicao = async (e) => {
    e.preventDefault();
    if (equipamentoEditando.nome && equipamentoEditando.status && equipamentoEditando.fabricante) {
      try {
        const response = await axios.put(`http://localhost:5000/api/equipamentos/${equipamentoEditando.id}`, {
          ...equipamentoEditando,
          atualizado_por: userId // <-- Quem atualizou
        });
        setEquipamentos((prev) =>
          prev.map((eq) => (eq.id === equipamentoEditando.id ? response.data : eq))
        );
        setEquipamentoEditando(null);
        setMostrarFormularioEdicao(false);
      } catch (error) {
        console.error('Erro ao editar equipamento', error);
      }
    }
  };

  const handleEditClick = (equipamento) => {
    setEquipamentoEditando(equipamento);
    setMostrarFormularioEdicao(true);
  };

  return (
    <div>
      <h2>Equipamentos</h2>

      <table>
        <thead>
          <tr>
            <th>Nome</th>
            <th>Status</th>
            <th>Fabricante</th>
            <th>AÃ§Ãµes</th>
          </tr>
        </thead>
        <tbody>
          {equipamentos.map((equipamento) => (
            <tr key={equipamento.id}>
              <td>{equipamento.nome}</td>
              <td>{equipamento.status}</td>
              <td>{equipamento.fabricante}</td>
              <td>
                <button onClick={() => handleEditClick(equipamento)}>Editar</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      <button onClick={() => setMostrarFormulario(true)}>Cadastrar Equipamento</button>

      {mostrarFormulario && (
        <div className="modal">
          <div className="modal-content">
            <h3>Cadastrar Novo Equipamento</h3>
            <form onSubmit={handleSubmitCadastro}>
              <div>
                <label>Nome:</label>
                <input type="text" name="nome" value={novoEquipamento.nome} onChange={handleChange} required />
              </div>
              <div>
                <label>Status:</label>
                <select name="status" value={novoEquipamento.status} onChange={handleChange} required>
                  <option value="">Selecione o status</option>
                  <option value="DisponÃ­vel">DisponÃ­vel</option>
                  <option value="Em ManutenÃ§Ã£o">Em ManutenÃ§Ã£o</option>
                  <option value="IndisponÃ­vel">IndisponÃ­vel</option>
                </select>
              </div>
              <div>
                <label>Fabricante:</label>
                <select name="fabricante" value={novoEquipamento.fabricante} onChange={handleChange} required>
                  <option value="">Selecione o fabricante</option>
                  {fabricantes.map((fab, i) => (
                    <option key={i} value={fab}>{fab}</option>
                  ))}
                </select>
              </div>
              <button type="submit">Cadastrar Equipamento</button>
              <button type="button" onClick={() => setMostrarFormulario(false)}>Cancelar</button>
            </form>
          </div>
        </div>
      )}

      {mostrarFormularioEdicao && equipamentoEditando && (
        <div className="modal">
          <div className="modal-content">
            <h3>Editar Equipamento</h3>
            <form onSubmit={handleSubmitEdicao}>
              <div>
                <label>Nome:</label>
                <input type="text" name="nome" value={equipamentoEditando.nome} onChange={handleChangeEdicao} required />
              </div>
              <div>
                <label>Status:</label>
                <select name="status" value={equipamentoEditando.status} onChange={handleChangeEdicao} required>
                  <option value="">Selecione o status</option>
                  <option value="DisponÃ­vel">DisponÃ­vel</option>
                  <option value="Em ManutenÃ§Ã£o">Em ManutenÃ§Ã£o</option>
                  <option value="IndisponÃ­vel">IndisponÃ­vel</option>
                </select>
              </div>
              <div>
                <label>Fabricante:</label>
                <select name="fabricante" value={equipamentoEditando.fabricante} onChange={handleChangeEdicao} required>
                  <option value="">Selecione o fabricante</option>
                  {fabricantes.map((fab, i) => (
                    <option key={i} value={fab}>{fab}</option>
                  ))}
                </select>
              </div>
              <button type="submit">Salvar AlteraÃ§Ãµes</button>
              <button type="button" onClick={() => setMostrarFormularioEdicao(false)}>Cancelar</button>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default Equipamentos;
